mapper.delete:
DELETE FROM Q_LIKE
WHERE
MEMBER_NO=#{memberNo} AND CONTENTS_NO=#{contentsNo}

mapper.select:
SELECT count(MEMBER_NO)
FROM Q_LIKE
WHERE CONTENTS_NO=#{contentsNo} AND MEMBER_NO=#{memberNo}

SELECT count(NO)
FROM Q_LIKE
WHERE CONTENTS_NO=#{contentsNo}

mapper.update:
UPDATE Q_VIEW
SET COUNT = COUNT + 1
WHERE CONTENTS_NO=#{contentsNo} AND MEMBER_NO=#{memberNo}

mapper.select:
SELECT count(*)
FROM Q_VIEW
WHERE CONTENTS_NO=#{contentsNo} AND MEMBER_NO=#{memberNo}

mapper.update:
UPDATE Q_KEYWORDS
SET MEMBER_NO=0
WHERE MEMBER_NO=#{memberNo}

UPDATE Q_LIKE
SET MEMBER_NO=0
WHERE MEMBER_NO=#{memberNo}

UPDATE Q_VIEW
SET MEMBER_NO=0
WHERE MEMBER_NO=#{memberNo}

UPDATE Q_CONTENTS_SAVED
SET MEMBER_NO=0
WHERE MEMBER_NO=#{memberNo}
